# Stack Implementation

```C++

#include <iostream>

using namespace std;

#define MAX 100

class Stack

{

public:

    int top;

    int a[MAX];

public:

    Stack()

    {

        top = -1;

    }

    bool empty()

    {

        return top < 0;

    }

    bool full()

    {

        return top == MAX - 1;

    }

    bool push(int x)

    {

        if (full())

        {

            cout << "Stack overflow!" << endl;

            return false;

        }

        else

        {

            a[++top] = x;

            return true;

        }

    }

    int pop()

    {

        if (empty())

        {

            cout << "Stack underflow!" << endl;

            return 0;

        }

        else

        {

            return a[top--];

        }

    }

    int peek()

    {

        if (empty())

        {

            cout << "Stack underflow!" << endl;

            return 0;

        }

        else

        {

            return a[top];

        }

    }

    int size()

    {

        return MAX;

    }

};




int main(){

    Stack s;

    s.push(4); // Insert 4

    s.push(5); // Inert 5

    cout<<s.pop()<<endl; // remove and return 5

    cout<<s.size()<<endl; // return and print size of the stack

    cout<<s.empty()<<endl; //return boolean which depends on if the stack is empty or no

    cout<<s.peek()<<endl; //return 4 aka the top most element but don't remove 4 from the stack

}

```

