# Reverse an Array using Stack

```C++

#include <iostream>

using namespace std;

#define MAX 100

class Stack

{

public:

    int top;

    char a[MAX];



public:

    Stack()

    {

        top = -1;

    }

    bool empty()

    {

        return top < 0;

    }

    bool full()

    {

        return top > MAX - 1;

    }

    bool push(int x)

    {

        if (full())

        {

            cout << "Stack overflow!" << endl;

            return false;

        }

        else

        {

            a[++top] = x;

            return true;

        }

    }

    int pop()

    {

        if (empty())

        {

            cout << "Stack underflow!" << endl;

            return 0;

        }

        else

        {

            return a[top--];

        }

    }

    int peek()

    {

        if (empty())

        {

            cout << "Stack underflow!" << endl;

            return 0;

        }

        else

        {

            return a[top];

        }

    }

    int size()

    {

        return MAX;

    }

};



int main()

{

    Stack s;

    int arr[5]; // declare array of 5 elements

    cout << "Enter array input" << endl;

    for (int i = 0; i < 5; i++)

    {

        cin >> arr[i]; // take and insert input of i element into array

    }



    cout << "Array in normal order:"<< endl;

    for (int i = 0; i < 5; i++)

    {

        cout << arr[i] << endl;

    }

    for (int i = 0; i < 5; i++)

    {

        s.push(arr[i]); // pushing the values in array to the stack

    }



    for (int i = 0; i < 5; i++)

    {

        arr[i] = s.pop(); // removing and inserting the top most elements from the stack into the array

    }

    cout << "Array in reversed order:" << endl;

    for (int i = 0; i < 5; i++)

    {

        cout << arr[i] << endl;

    }

}

```

