export interface NavLink {
    name: string;
    href: string;
}
export interface NavDropdown {
    name: string;
    subItems: Array<NavLink>;
}
export declare function isNavDropdown(link: NavLink | NavDropdown): link is NavDropdown;
export interface NavConfig {
    title: string;
    logo?: string;
    version?: string;
    links: Array<NavLink | NavDropdown>;
    search?: SearchProviderConfig;
    social?: Array<SocialLink>;
}
export interface AuthorConfig {
    name: string;
    url: string;
    logo: string;
}
export interface ThemeConfig {
    defaultTheme: "dark" | "light";
    themeToggleIcon: string;
}
export type SocialPlatform = "github" | "discord";
export interface SocialLink {
    label: SocialPlatform;
    href: string;
}
export type SearchProvider = "algolia" | "kbar";
export interface SearchProviderConfig {
    provider: SearchProvider;
    config: object;
}
interface SharedFields {
    title?: string;
    description?: string;
    image?: string;
    layout: string;
    showEditLink?: boolean;
    showToc?: boolean;
    isDraft?: boolean;
    data: Array<string>;
}
interface ComputedFields {
    url_path: string;
    edit_url?: string;
}
export interface Page extends SharedFields, ComputedFields {
}
export interface Blog extends SharedFields, ComputedFields {
    created: string;
    authors?: Array<string>;
    tags?: Array<string>;
}
export {};
